r2rml:
@prefix : <http://ejemplo.org/mapping/>.
@prefix rr: <http://www.w3.org/ns/r2rml#>.
@prefix ex: <http://ejemplo.org/>.
@prefix xsd: <http://www.w3.org/2001/XMLSchema#>.


:TriplesMap_emp a rr:TriplesMap;
    rr:logicalTable [
        rr:tableName "emp"
    ];
    rr:subjectMap [
        rr:template "http://ejemplo.org/emp/{emp_no}";
        rr:class ex:Emp
    ];
    rr:predicateObjectMap [
        rr:predicate ex:apellido;
        rr:objectMap [
            rr:column "apellido";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:oficio;
        rr:objectMap [
            rr:column "oficio";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:dir;
        rr:objectMap [
            rr:column "dir";
            rr:datatype xsd:integer
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:fecha_alt;
        rr:objectMap [
            rr:column "fecha_alt";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:salario;
        rr:objectMap [
            rr:column "salario";
            rr:datatype xsd:decimal
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:comision;
        rr:objectMap [
            rr:column "comision";
            rr:datatype xsd:decimal
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:dept_no;
        rr:objectMap [
            rr:column "dept_no";
            rr:datatype xsd:integer
        ]
    ].
