r2rml:
@prefix : <http://ejemplo.org/mapping/>.
@prefix rr: <http://www.w3.org/ns/r2rml#>.
@prefix ex: <http://ejemplo.org/>.
@prefix xsd: <http://www.w3.org/2001/XMLSchema#>.


:TriplesMap_enfermo a rr:TriplesMap;
    rr:logicalTable [
        rr:tableName "enfermo"
    ];
    rr:subjectMap [
        rr:template "http://ejemplo.org/enfermo/{inscripcion}";
        rr:class ex:Enfermo
    ];
    rr:predicateObjectMap [
        rr:predicate ex:apellido;
        rr:objectMap [
            rr:column "apellido";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:direccion;
        rr:objectMap [
            rr:column "direccion";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:fecha_nac;
        rr:objectMap [
            rr:column "fecha_nac";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:s;
        rr:objectMap [
            rr:column "s";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:nss;
        rr:objectMap [
            rr:column "nss";
            rr:datatype xsd:integer
        ]
    ].
