r2rml:
@prefix : <http://ejemplo.org/mapping/>.
@prefix rr: <http://www.w3.org/ns/r2rml#>.
@prefix ex: <http://ejemplo.org/>.
@prefix xsd: <http://www.w3.org/2001/XMLSchema#>.


:TriplesMap_emp a rr:TriplesMap;
    rr:logicalTable [
        rr:tableName "emp"
    ];
    rr:subjectMap [
        rr:template "http://ejemplo.org/emp/{emp_no}";
        rr:class ex:Emp
    ];
    rr:predicateObjectMap [
        rr:predicate ex:apellido;
        rr:objectMap [
            rr:column "apellido";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:oficio;
        rr:objectMap [
            rr:column "oficio";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:dir;
        rr:objectMap [
            rr:column "dir";
            rr:datatype xsd:integer
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:fecha_alt;
        rr:objectMap [
            rr:column "fecha_alt";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:salario;
        rr:objectMap [
            rr:column "salario";
            rr:datatype xsd:decimal
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:comision;
        rr:objectMap [
            rr:column "comision";
            rr:datatype xsd:decimal
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:dept_no;
        rr:objectMap [
            rr:column "dept_no";
            rr:datatype xsd:integer
        ]
    ].

:TriplesMap_doctor a rr:TriplesMap;
    rr:logicalTable [
        rr:tableName "doctor"
    ];
    rr:subjectMap [
        rr:template "http://ejemplo.org/doctor/{doctor_no}";
        rr:class ex:Doctor
    ];
    rr:predicateObjectMap [
        rr:predicate ex:hospital_cod;
        rr:objectMap [
            rr:column "hospital_cod";
            rr:datatype xsd:integer
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:apellido;
        rr:objectMap [
            rr:column "apellido";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:especialidad;
        rr:objectMap [
            rr:column "especialidad";
            rr:datatype xsd:string
        ]
    ].

:TriplesMap_sala a rr:TriplesMap;
    rr:logicalTable [
        rr:tableName "sala"
    ];
    rr:subjectMap [
        rr:template "http://ejemplo.org/sala/{sala_cod}";
        rr:class ex:Sala
    ];
    rr:predicateObjectMap [
        rr:predicate ex:hospital_cod;
        rr:objectMap [
            rr:column "hospital_cod";
            rr:datatype xsd:integer
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:nombre;
        rr:objectMap [
            rr:column "nombre";
            rr:datatype xsd:string
        ]
    ];
    rr:predicateObjectMap [
        rr:predicate ex:num_cama;
        rr:objectMap [
            rr:column "num_cama";
            rr:datatype xsd:integer
        ]
    ].
